import React from 'react';
import {Elements} from '@stripe/react-stripe-js';
import {loadStripe} from '@stripe/stripe-js';

import CheckoutForm from '../stripe-checkout-form/StripeCheckout';

// Make sure to call `loadStripe` outside of a component’s render to avoid
// recreating the `Stripe` object on every render.
const stripePromise = loadStripe('pk_test_51KksksENpvpK9J8psPLe7fxjiNMjwNLM7GxWowCDdDm1XTaFSs8LKrK1Fef5nFLKOa8SSMVsRJ6a1kQXt7HrAsse00qgExWD6S');

const StripeButton = () => {
  const clientsecret = (async () => {
    const response = await fetch('/secret');
    const {client_secret: clientSecret} = await response.json();
    // Render the Payment Element using the clientSecret
    return clientSecret
  })();

  const options = {
    // passing the client secret obtained in step 2
    clientSecret: clientsecret,
    // Fully customizable with appearance API.
    appearance: {/*...*/},
  };

  return (
    <Elements stripe={stripePromise} options={options}>
      <CheckoutForm />
    </Elements>
  );
};

export default StripeButton
