import ShopActionTypes from "./shopTypes";
import { db, convertCollectionsSnapshotToMap } from "../../firebase/firebaseUtils";
import { onSnapshot, collection } from 'firebase/firestore';

export const fetchCollectionsStart = () => ({
    type: ShopActionTypes.FETCH_COLLECTIONS_START
})

export const fetchCollectionsSuccess = collectionsMap => ({
    type:ShopActionTypes.FETCH_COLLECTIONS_SUCCESS,
    payload: collectionsMap
})

export const fetchCollectionsFailure = errorMessage => ({
    type: ShopActionTypes.FETCH_COLLECTIONS_FAILURE,
    payload: errorMessage
})

export const fetchCollectionsStartAsync = () => {
    return dispatch => {
        const collectionRef = collection(db, 'collections')
        dispatch(fetchCollectionsStart())
        onSnapshot(collectionRef, async (snapshot) => {
            const collectionsMap = convertCollectionsSnapshotToMap(snapshot)
            dispatch(fetchCollectionsSuccess(collectionsMap))
        // once this asynchronous call resolves, we are going to dispatch this collectionsMap 
        // to fetchCollectionsSuccess function action
        }).catch(error => dispatch(fetchCollectionsFailure(error.message)))
    }
} // we are utilising redux-thunk to dispatch function as action instead of an object
// and redux-thunk allows us to dispatch multiple actions in the same aynschronous call
